INSERT INTO `Nonprofits` (`id`, `orgName`, `mission`, `address`, `city`, `state`, `zip`, `country`, `orgFocus`, `url`, `createdAt`, `updatedAt`, `UserId`) VALUES (153,'Wellspring House, Inc.','<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><strong style=\"mso-bidi-font-weight: normal;\"><span style=\"font-size: 12pt; font-family: Arial;\">Part Time Medical Terminology and Office Procedures Teacher</span></strong></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><span style=\"font-size: 12pt; font-family: Arial;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><strong style=\"mso-bidi-font-weight: normal;\"><span style=\"font-size: 12pt; font-family: Arial;\">Wellspring House, Inc.</span></strong><span style=\"font-size: 12pt; font-family: Arial;\"> seeks a Part-time Medical Terminology and Office Procedures Teacher.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><span style=\"font-size: 12pt; font-family: Arial;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><span style=\"font-size: 12pt; font-family: Arial;\">The Mediclerk Program is an 11-week training program located in Salem, MA. The first session begins November 3, 2008 ending in February, 2009.<span style=\"mso-spacerun: yes;\">&nbsp; </span>Students are studying for entry level positions in the healthcare field.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><span style=\"font-size: 12pt; font-family: Arial;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><span style=\"font-size: 12pt; font-family: Arial;\">The program is for (3) annual sessions ~ 6 hours per week class time, 6 hours per week preparation time, and 1 hour per week staff meeting.<span style=\"mso-spacerun: yes;\">&nbsp; </span>The candidate needs a good working knowledge of medical terminology and office practices as well as experience and education as a teacher.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><span style=\"font-size: 12pt; font-family: Arial;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><span style=\"font-size: 12pt; font-family: Arial;\">For more information: <a href=\"http://wellspringhouse.org/educationandjob/mediclerk.html\"><span style=\"color: #800080;\">http://wellspringhouse.org/educationandjob/mediclerk.html</span></a></span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><span style=\"font-size: 12pt; font-family: Arial;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><span style=\"font-size: 12pt; font-family: Arial;\">Please send a cover letter and your resume to Sandy Sabo, Program Coordinator, at <a href=\"mailto:Mediclerk@wellspringhouse.org\">Mediclerk@wellspringhouse.org</a></span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><span style=\"font-size: 12pt; font-family: Arial;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><span style=\"font-size: 12pt; font-family: Arial;\">No phone calls please.<span style=\"mso-spacerun: yes;\">&nbsp; </span>AA/EOE</span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0in 0in 0pt;\"><span style=\"font-size: 12pt; font-family: Arial;\">&nbsp;</span></p>','302 Essex Avenue','Gloucester','Ma','1930','United States','','http://www.wellspringhouse.org/','2019-11-09 07:54:42','0000-00-00 00:00:00',1);
