INSERT INTO `Nonprofits` (`id`, `orgName`, `mission`, `address`, `city`, `state`, `zip`, `country`, `orgFocus`, `url`, `createdAt`, `updatedAt`, `UserId`) VALUES (192,'Plaza Community Services','<p align=\"justify\"><span class=\"style3\"><strong><span style=\"font-family: arial; color: #ed1c24; font-size: small;\">Mission</span></strong></span></p>\r\n<p><span style=\"font-family: arial; font-size: small;\">Our mission is to support children, youth, and families in need to develop and grow within a nurturing, spiritual environment by providing effective, high quality health, education, and other community services. </span></p>\r\n<p>&nbsp;</p>\r\n<p class=\"style3\"><span style=\"font-family: arial;\"><span style=\"font-size: small;\"><strong>Organizational Values</strong> </span></span></p>\r\n<p><span style=\"font-family: arial;\"><span style=\"font-size: small;\"><strong>Family:</strong>&nbsp; We value strong, loving families as the most basic unit to nurture the human spirit.&nbsp; </span></span></p>\r\n<p><span style=\"font-family: arial;\"><span style=\"font-size: small;\"><strong>Community:</strong>&nbsp; We value communities that provide sustainable networks for health and education, as well as spiritual and economic development in support of families and individuals.&nbsp; </span></span></p>\r\n<p><span style=\"font-family: arial;\"><span style=\"font-size: small;\"><strong>Education:</strong>&nbsp; We value education as essential to break the cycle of poverty and to nurture individuals who will contribute to a healthy, sustainable community.</span></span></p>\r\n<p><span style=\"font-family: arial;\"><span style=\"font-size: small;\"><strong>Respect &amp; Dignity:</strong>&nbsp; We value each person as a child of God, and afford him/her the respect and dignity this status demands.&nbsp; </span></span></p>\r\n<p><span style=\"font-family: arial;\"><span style=\"font-size: small;\"><strong>Cultural Competency:&nbsp;</strong> We value the diversity of God&rsquo;s kingdom and the diverse cultures of the people we serve; for these reasons we treat each person with understanding and respect.&nbsp; </span></span></p>\r\n<p><span style=\"font-family: arial;\"><span style=\"font-size: small;\"><strong>Language:</strong>&nbsp; We value the first languages of all the families we serve and actively support first and other language development through program curricula, communications, and staff development.</span></span></p>','4018 City Terrace Drive','Los Angeles','Ca','90063','','Child Care, Children and Youth, Drug and Alcohol Treatment, Fathers, Gang Prevention and Intervention, Housing, Mental Health, Network of Nonprofit Organizations, Youth Services','http://www.plazacs.org','2019-11-09 07:54:42','0000-00-00 00:00:00',1);
