INSERT INTO `Nonprofits` (`id`, `orgName`, `mission`, `address`, `city`, `state`, `zip`, `country`, `orgFocus`, `url`, `createdAt`, `updatedAt`, `UserId`) VALUES (211,'Mexican American Legal Defense and Educational Fund (MALDEF)','<p>Founded in 1968, MALDEF is the nation&rsquo;s leading non-profit Latino legal organization. Often described as the &ldquo;law firm of the Latino community,&rdquo; MALDEF promotes equality and justice through litigation, advocacy, public policy, and community education in the areas of employment, immigrants&rsquo; rights, voting rights, education, and language rights.</p>\r\n<p>MALDEF strives to implement programs that are structured to bring Latinos into the mainstream of American political and socio-economic life; providing better educational opportunities; encouraging participation in all aspects of society; and offering a positive vision for the future. Unique to MALDEF is an approach that combines advocacy, educational outreach, and litigation strategies to achieve socio-economic change.</p>','634 South Spring Street, 11th Floor','Los Angeles','Ca','90014','United States','Education and Academia, Human Rights and Civil Liberties, Immigration, Law and Legal Assistance, Race and Ethnicity','http://www.maldef.org','2019-11-09 07:54:42','0000-00-00 00:00:00',1);
INSERT INTO `Nonprofits` (`id`, `orgName`, `mission`, `address`, `city`, `state`, `zip`, `country`, `orgFocus`, `url`, `createdAt`, `updatedAt`, `UserId`) VALUES (221,'Corporate Accountability International','<p>\r\n<p class=\"MsoBodyText\"><span style=\"font-size: 11.0pt; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;;\">Since\r\n1977, Corporate Accountability International\r\nhas successfully challenged corporations like Nestl&eacute;, General Electric, and\r\nPhilip Morris to halt abusive practices that threaten human rights, public\r\nhealth, the environment and our democracy. The organization is expanding&mdash;and\r\nleading campaigns challenging some of the world&rsquo;s most powerful industries,\r\nincluding food and agriculture, water, and Big Tobacco. </span></p>\r\n<p class=\"MsoBodyText\"><span style=\"font-size: 11.0pt; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;;\"><strong>Think Outside the\r\nBottle</strong> exposes the truth behind bottled water marketing and defends the\r\nhuman right to water in the face of increasing corporate control. <strong>Value\r\n[the] Meal </strong>challenges the fast food industry to stop driving an epidemic of\r\ndiet-related disease, and our campaign to <strong>Challenge Big Tobacco</strong> protects\r\nthe first-ever corporate accountability treaty from tobacco industry\r\ninterference.&nbsp;<em></em></span></p>\r\n</p>','10 Milk St., Suite 610','Boston','Ma','2108','United States','Community Organizing / Advocacy, International Relations','http://www.stopcorporateabuse.org','2019-11-09 07:54:42','0000-00-00 00:00:00',1);
