INSERT INTO `Nonprofits` (`id`, `orgName`, `mission`, `address`, `city`, `state`, `zip`, `country`, `orgFocus`, `url`, `createdAt`, `updatedAt`, `UserId`) VALUES (252,'Journey to Solidarity','<p><span style=\"font-size: 13px; line-height: 17px;\"><span style=\"color: #333399;\">Journey to Solidarity is a talented and diverse team of individuals who share a passion for helping people and working together. There is a common goal to create better lives and social solidarity throughout the world without demanding money to those we serve or by being political. The project was originally started by a Chiropractor, Dr. Jay Breitlow, who has a vision to share the best and most natural form of healthcare with the world, while creating long lasting social ties to communities of need. Journey to Solidarity is a public 501(c)(3) non-profit corporation.</span></span></p>','301 Cottage Grove Ave. SE','Cedar Rapids','Io','52403','United States','Other Service Organization','http://www.journeytosolidarity.org','2019-11-09 07:54:42','0000-00-00 00:00:00',1);
