INSERT INTO `Nonprofits` (`id`, `orgName`, `mission`, `address`, `city`, `state`, `zip`, `country`, `orgFocus`, `url`, `createdAt`, `updatedAt`, `UserId`) VALUES (307,'Foster Parent Advocacy Foundation Inc','<p><span style=\"font-size: large; color: #ff0000; font-family: TimesNewRoman;\"><span style=\"font-size: large; color: #ff0000; font-family: TimesNewRoman;\"><span style=\"font-size: large; color: #ff0000; font-family: TimesNewRoman;\">\r\n<p align=\"left\">It is our <span style=\"font-size: small;\">mission to help improve the quality of life for foster</span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\">parents and children in foster care. We will be responsible for</span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\">addressing the social service needs of the children in care, as well as the</span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\">foster parents. At the same time we look for constructive ways to make</span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\">things better with the foster care agencies to provide quality service. We</span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\">are consistently open to finding new ways to achieve the mission and</span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\">constantly seek ways to do our work and meet the needs of those we</span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\">serve.</span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\">We use our diversity to gain new perspectives and collaborate and</span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\">help strengthen foster care agencies through training and organizational</span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\">development. Our standard is excellence and we will always work to</span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\">exceed expectations. We strive toward continuous improvement in all</span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\">we do!</span></p>\r\n<p><span style=\"font-size: small;\">We thank you in advance for your time, attention and support.</span></p>\r\n</span></span></span></p>','711 west 168 street','new york','Ne','10032','United States','Children and Youth','http://www.fpafoundation.org','2019-11-09 07:54:42','0000-00-00 00:00:00',1);
INSERT INTO `Nonprofits` (`id`, `orgName`, `mission`, `address`, `city`, `state`, `zip`, `country`, `orgFocus`, `url`, `createdAt`, `updatedAt`, `UserId`) VALUES (309,'Volunteer Doctors Coalition','<p>We are a newly formed Non Profit Organization, consummating a union between Healthcare Professionals, and children from indigent families, with cardiac disease. These children require surgery to sustain their lives, and bring them to good health.</p>\r\n<p>Since our organization is philanthropic, we are attempting to assist others in becoming, or utilizing their focus as&nbsp;philanthropists.</p>\r\n<p>Children are our future, so we want to invest in making, or keeping them healthy.</p>\r\n<p>In addition, our goal is to&nbsp;Employ \"Displaced Veterans\" to package \"Care Paks\" for the children. These \"Care Paks\" will contain basic necessities for healthy living.</p>\r\n<p>We will be hosting events to raise awareness and donors.</p>\r\n<p>Our first event is for&nbsp;2 weeks,&nbsp;Dec. 28, 2009 - Jan. 10, 2010 to HAWAII. \"Cruising4Cash\" as a part of our $5 million campaign to raise funds for children with cardiac disease, requiring surgery. These funds will provide a Triage Center for implementation of free surgery for these children.</p>','32 Lembeck Av. 1st floor','Jersey City','Ne','7305','United States','Community Service and Volunteering, Health and Medicine, Homelessness, Poverty and Hunger, Service Organization, Teaching, Training, Veterans Issues','','2019-11-09 07:54:42','0000-00-00 00:00:00',1);
